mapper.insert:
insert
  into board
     (
       board_no
     , board_title
     , board_writer
     , board_content
     , origin_name
     , change_name
     )
values
     (
       seq_bno.nextval
     , #{boardTitle}
     , #{boardWriter}
     , #{boardContent}
     , #{originName}
     , #{changeName}
     )

mapper.update:
update board
   set bStatus = 'N'
 where board_no = #{boardNo}

update 
	   board
   set board_title = #{boardTitle}
     , board_content = #{boardContent} 
     , origin_name = #{originName}
     , change_name = #{changeName}
 where board_no = #{boardNo}

mapper.insert:
insert
  into reply
     (
       reply_no
     , reply_content
     , ref_bno
     , reply_writer  
     )
values
     (
       seq_rno.nextval
     , #{replyContent}
     , #{refBoardNo}
     , #{replyWriter}  
     )

mapper.update:
update
       reply
   set reply_content = ${replyContent}
 where reply_no = ${replyNo}
   and r_status = 'Y'
