mapper.insert:
insert 
  into calendar
     (
       calendar_no
     , student_no
     , cal_title
     , cal_start_date
     , cal_end_date
     , background_color
     )
values
     (
       seq_cal.nextval
     , #{studentNo}
     , #{calTitle}
     , #{calStartDate}
     , #{calEndDate}
     , #{backgroundColor}
     )
