mapper.insert:
	insert
	  into class
	     (
	      class_no
	    , class_num
	    , prof_no
	    , class_name
	    , class_type
	    , class_score
	    , class_semester
	    , class_grade
	    , class_room
	    , class_time
	    , class_people
	    , class_etc
	    , origin_name
	    , change_name
	    , enroll_date
	    )
    values
	    (seq_class.nextval
	     ,#{classNum}
	     ,#{profNo}
	     ,#{className}
	     ,#{classType}
	     ,#{classScore}
	     ,#{classSemester}
	     ,#{classGrade}
	     ,#{classRoom}
	     ,#{classTime}
	     ,#{classPeople}
	     ,#{classEtc}
	     ,#{originName}
	     ,#{changeName}
	     ,TO_CHAR(SYSDATE,'YYYY')
	     )

mapper.update:
   update
	      class
      set class_num = #{classNum}
	    , class_name = #{className}
	    , class_type = #{classType}
	    , class_score = #{classScore}
	    , class_semester = #{classSemester}
	    , class_grade = #{classGrade}
	    , class_room = #{classRoom}
	    , class_time = #{classTime}
	    , class_people = #{classPeople}
	    , class_etc = #{classEtc}
	    , origin_name = #{originName}
	    , change_name = #{changeName}
	    , enroll_date = TO_CHAR(SYSDATE,'YYYY')
    where class_no = #{classNo}

