mapper.insert:
	insert
	  into class
	     (
	      class_no
	    , class_num
	    , prof_no
	    , class_name
	    , class_type
	    , class_score
	    , class_semester
	    , class_grade
	    , class_room
	    , class_time
	    , class_people
	    , class_etc
	    , origin_name
	    , change_name
	    )
    values
	    (seq_class.nextval
	     ,#{classNum}
	     ,null
	     ,#{className}
	     ,#{classType}
	     ,#{classScore}
	     ,#{classSemester}
	     ,#{classGrade}
	     ,#{classRoom}
	     ,#{classTime}
	     ,#{classPeople}
	     ,#{classEtc}
	     ,#{originName}
	     ,#{changeName}
	     )
