mapper.insert:
insert
  into grade
  	 ( 
  	   class_no
  	 , student_no
  	 , grade
  	 , term_no
  	 , mid_point
  	 , fin_point
  	 , project_point
  	 , atten_point
  	 , total_point
  	 )
values
	(
	  #{classNo}
	, #{studentNo}
	, #{grade}
	, (SELECT CLASS_SEMESTER FROM class WHERE class_no = #{classNo})
	, #{midPoint}
	, #{finPoint}
	, #{projectPoint}
	, #{attenPoint}
	, #{totalPoint}
	)

mapper.update:
   update
	      grade
      set mid_point = #{midPoint}
	    , fin_point = #{finPoint}
	    , project_point = #{projectPoint}
	    , atten_point = #{attenPoint}
	    , total_point = #{totalPoint}
	    , grade = #{grade}
    where student_no = #{studentNo}
      and class_no = #{classNo}
