mapper.insert:
insert
  into member
     (
       member_no
     , member_id
     , member_pwd
     , member_name
     , member_ssn
     , m_phone
     , m_email
     , m_address
     , m_type  
     )
values
     (
       seq_mno.nextval
     , 'pr'||to_char(seq_user_no.nextval, 'FM0000')
     , #{memberPwd}
     , #{memberName}
     , #{memberSsn}
     , #{mePhone}
     , #{meEmail}
     , #{meAddress}
     , #{meType}
     )

mapper.update:
update member
   set member_pwd = #{userPwd}
 where member_id = #{memberId}

update member
   set m_address = #{meAddress}
 where member_id = #{memberId}
