     , member_ssn
     , m_phone
     , m_email
     , m_address
     , m_type  
     )
values
     (
       seq_mno.nextval
     , 'pr'||to_char(seq_user_no.nextval, 'FM0000')
     , #{memberPwd}
     , #{memberName}
     , #{memberSsn}
     , #{mePhone}
     , #{meEmail}
     , #{meAddress}
     , #{meType}
     )

