mapper.insert:
insert
  into member
     (
       member_no
     , member_id
     , member_pwd
     , member_name
     , member_ssn
     , m_phone
     , m_email
     , m_address
     , m_type  
     )
values
     (
       seq_mno.nextval
     , to_char(sysdate, 'yyMMdd') || floor(dbms_random.value(10000,99999))
     , #{memberPwd}
     , #{memberName}
     , #{memberSsn}
     , #{mPhone}
     , #{mEmail}
     , #{mAddress}
     , #{mType}
     )

mapper.update:
update member
   set member_pwd = #{userPwd}
 where member_id = #{memberId}
